import React from 'react'

class ScrollingWrapper extends React.Component {
    state = { hasScrolled: false }
  
    componentDidMount() {
      this.scrollingWrapper.addEventListener('scroll', this.onScroll)
    }
  
    onScroll = () => {
      if (this.scrollingWrapper.scrollTop > 100 && !this.state.hasScrolled) {
        this.setState({ hasScrolled: true })
      } else if (this.scrollingWrapper.scrollTop < 100 && this.state.hasScrolled) {
        this.setState({ hasScrolled: false })
      }
    }
  
    scrollToTop = () => {
      this.scrollingWrapper.scrollTop = 0
    }
  
    reference = id => ref => {
      this[id] = ref
    }
  
    render() {
      return (
        <React.Fragment>
          {this.state.hasScrolled && (
            <div className="ScrollToTopIconContainer" onClick={this.scrollToTop}>
              <div>^</div>
              <div className="button"><button>BACK TO TOP</button></div>
            </div>
          )}
          <div className="ScrollingWrapperContainer" ref={this.reference('scrollingWrapper')}>
            {this.props.children}
          </div>
        </React.Fragment>
      )
    }
  }
  
  export default ScrollingWrapper
